    public static int solve(int[] A) {
        if(A.length == 0) return 0;
        if(A.length == 1) return A[0];
        Arrays.sort(A);
        int[] preSum = new int[A.length];
        preSum[0] = A[0];
        int result =A[0];
        int MOD = 1000000007;
        for(int i=1; i < A.length; i++){
            preSum[i] = (preSum[i-1]+A[i])%MOD;
            result = (result+preSum[i])%MOD;
        }
        return result%MOD;
    }
